src/components/Counter.jsx:
import { useState, useEffect } from 'react';

function Counter() {
  const [count, setCount] = useState(() => {
    const savedCount = localStorage.getItem('counterValue');
    return savedCount !== null ? parseInt(savedCount, 10) : 0;
  });

  useEffect(() => {
    localStorage.setItem('counterValue', count.toString());
  }, [count]);

  const increment = () => setCount(count + 1);
  const decrement = () => setCount(count - 1);
  const reset = () => setCount(0);

  return (
    <div className="flex flex-col items-center space-y-6">
      <div className="text-6xl font-bold text-indigo-600 mb-4" data-testid="counter-display">
        {count}
      </div>
      <div className="flex gap-4 flex-wrap justify-center">
        <button
          onClick={decrement}
          className="px-6 py-3 bg-red-500 hover:bg-red-600 text-white font-semibold rounded-lg shadow-md transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-red-400 focus:ring-offset-2"
          data-testid="decrement-button"
        >
          Decrement
        </button>
        <button
          onClick={reset}
          className="px-6 py-3 bg-gray-500 hover:bg-gray-600 text-white font-semibold rounded-lg shadow-md transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:ring-offset-2"
          data-testid="reset-button"
        >
          Reset
        </button>
        <button
          onClick={increment}
          className="px-6 py-3 bg-green-500 hover:bg-green-600 text-white font-semibold rounded-lg shadow-md transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-green-400 focus:ring-offset-2"
          data-testid="increment-button"
        >
          Increment
        </button>
      </div>
    </div>
  );
}

export default Counter;

src/__tests__/counter.test.js:
import { describe, it, expect, beforeEach, vi } from 'vitest';
import { render, screen, fireEvent } from '@testing-library/react';
import Counter from '../components/Counter';

describe('Counter Component', () => {
  beforeEach(() => {
    localStorage.clear();
  });

  it('renders the counter component', () => {
    render(<Counter />);
    const counterDisplay = screen.getByTestId('counter-display');
    expect(counterDisplay).toBeInTheDocument();
  });

  it('initializes counter at 0', () => {
    render(<Counter />);
    const counterDisplay = screen.getByTestId('counter-display');
    expect(counterDisplay).toHaveTextContent('0');
  });

  it('increments the counter when increment button is clicked', () => {
    render(<Counter />);
    const incrementButton = screen.getByTestId('increment-button');
    const counterDisplay = screen.getByTestId('counter-display');

    fireEvent.click(incrementButton);
    expect(counterDisplay).toHaveTextContent('1');

    fireEvent.click(incrementButton);
    expect(counterDisplay).toHaveTextContent('2');
  });

  it('decrements the counter when decrement button is clicked', () => {
    render(<Counter />);
    const decrementButton = screen.getByTestId('decrement-button');
    const counterDisplay = screen.getByTestId('counter-display');

    fireEvent.click(decrementButton);
    expect(counterDisplay).toHaveTextContent('-1');

    fireEvent.click(decrementButton);
    expect(counterDisplay).toHaveTextContent('-2');
  });

  it('resets the counter to zero when reset button is clicked', () => {
    render(<Counter />);
    const incrementButton = screen.getByTestId('increment-button');
    const resetButton = screen.getByTestId('reset-button');
    const counterDisplay = screen.getByTestId('counter-display');

    fireEvent.click(incrementButton);
    fireEvent.click(incrementButton);
    fireEvent.click(incrementButton);
    expect(counterDisplay).toHaveTextContent('3');

    fireEvent.click(resetButton);
    expect(counterDisplay).toHaveTextContent('0');
  });

  it('persists counter value in localStorage', () => {
    render(<Counter />);
    const incrementButton = screen.getByTestId('increment-button');

    fireEvent.click(incrementButton);
    fireEvent.click(incrementButton);
    fireEvent.click(incrementButton);

    expect(localStorage.getItem('counterValue')).toBe('3');
  });

  it('loads counter value from localStorage on mount', () => {
    localStorage.setItem('counterValue', '42');

    render(<Counter />);
    const counterDisplay = screen.getByTestId('counter-display');

    expect(counterDisplay).toHaveTextContent('42');
  });

  it('updates localStorage when counter changes', () => {
    render(<Counter />);
    const incrementButton = screen.getByTestId('increment-button');
    const decrementButton = screen.getByTestId('decrement-button');
    const resetButton = screen.getByTestId('reset-button');

    fireEvent.click(incrementButton);
    expect(localStorage.getItem('counterValue')).toBe('1');

    fireEvent.click(decrementButton);
    expect(localStorage.getItem('counterValue')).toBe('0');

    fireEvent.click(incrementButton);
    fireEvent.click(incrementButton);
    expect(localStorage.getItem('counterValue')).toBe('2');

    fireEvent.click(resetButton);
    expect(localStorage.getItem('counterValue')).toBe('0');
  });
});
